import {combineReducers} from 'redux';

const initialState = {
    frutas: []
}

function reducer(state = initialState , action){
    if(action.type === "ADICIONAR"){
        return { frutas: [...state.frutas, {...action.value }] }
    }
    else{
        return state;
    }
}

const textoInicial = {
    titulo: ["texto inicial"]
}

function tituloReducer(state = textoInicial , action){
    if(action.type === "TROCAR"){
        return { }
    }else{
        return state;
    }
}

const reducers = combineReducers ({reducer, tituloReducer});

export default reducers;
