import React from 'react';
import { useSelector } from 'react-redux';

function App() {
  const state = useSelector((state) => state.frutas)
  console.log(state);

  return (
    <div className="App" style={{textAlign:'center', color: 'tomato', fontWeight: 'bolder'}}>
     Aqui vamos alterar nosso app
     <br />
     {state.map((fruta) =>{
       return(
         <li>{fruta.nome}</li>
       )
     })}
     </div>
  );
}

export default App;
