import React from 'react';
import { useSelector, useDispatch } from 'react-redux';

function App() {

  const [inputFrutas, setFrutas] = React.useState("");

  //redux
  const state = useSelector((state) => state.frutas)
  // console.log(state);
  const dispatch = useDispatch();

  function adicionarFrutas (event){
    event.preventDefault();

    const objFrutas = {
      nome: inputFrutas
    }

      dispatch({type: "ADICIONAR", value:objFrutas })

  }

  return (
    <div className="App" style={{textAlign:'center', color: 'tomato', fontWeight: 'bolder'}}>
     Aqui vamos alterar nosso app
     <br />

     <form onSubmit={adicionarFrutas}>

       <input 
       placeholder="Digite uma fruta: " 
       value={inputFrutas} 
       onChange={(event) => setFrutas(event.target.value)}/>

      <button style={{background: "#000080", color: 'white', borderRadius:"5px"}}>Enviar</button>
     </form>

     {state.map((fruta, index) =>{
       return(
         <li key={index}>{fruta.nome}</li>
       )
     })}
     </div>
  );
}

export default App;
