import React from 'react';
import { useSelector } from 'react-redux';

function App() {

  const [inputFrutas, setFrutas] = React.useState("");


  //redux
  const state = useSelector((state) => state.frutas)
  console.log(state);

  return (
    <div className="App" style={{textAlign:'center', color: 'tomato', fontWeight: 'bolder'}}>
     Aqui vamos alterar nosso app
     <br />

     <form>

       <input placeholder="Digite uma fruta: " 
       value={inputFrutas} 
       onChange={(event) => setInputFrutas(event.target.value)}/>

     </form>

     {state.map((fruta, index) =>{
       return(
         <li key={index}>{fruta.nome}</li>
       )
     })}
     </div>
  );
}

export default App;
